@push('footer-style')
    <style>
        #content {
            width: 100%;
            max-width: 1600px;
            margin: 0 auto 8% auto;
        }

        #content {
            width: 75% !important;
        }

        .title {
            text-align: center;
            font-size: 210%;
            margin-bottom: 3%;
            font-weight: 600;
        }

        .title {
            line-height: 1.4;
            width: 50%;
            margin: 5% auto !important;
        }

        .options-holder {
            display: flex;
            align-items: center;
            justify-content: space-around;
        }

        .option-holder {
            position: relative;
            width: 36%;
            border-radius: 8px;
            cursor: pointer;
            text-align: center;
        }

        .option-img {
            width: 100%;
            height: 225px;
            border-radius: 8px;
            object-fit: cover;
        }

        .option-info-holder {
            text-align: left;
            padding: 18px 0;
        }

        .option-title {
            display: flex;
            align-items: center;
            font-weight: 600;
            font-size: 140%;
            margin-bottom: 11px;
        }

        .option-badge-free {
            background-color: var(--green);
        }

        .option-badge {
            padding: 2px 8px;
            font-size: 14px;
            font-weight: 500;
            border-radius: 50px;
            margin-left: 10px;
        }

        .option-txt {
            color: gray;
            font-size: 95%;
        }

        .option-btn {
            display: flex;
            align-items: center;
            font-weight: 500;
            padding: 5px 25px;
            border-radius: 8px;
            border: 1px solid var(--black);
            color: inherit;
            text-decoration: none;
            margin-top: 15px;
            width: fit-content;
        }

        .option-btn-img {
            width: 10px;
            margin-left: 8px;
            transform: rotateZ(180deg);
        }
    </style>
@endpush
